function getDownloadCount_userProfile( $user_name = false ) {
	global $db;

  	if( $user_name ) {

      	$user_name = $db->safesql($user_name);

      	$count = $db->super_query( "SELECT count(*) as cnt FROM " . USERPREFIX . "_download_log WHERE user_name = '{$user_name}'" );

      	return $count;

    }

  	return false;
}
